\begin{verbatim}
var play = "X";	 	       	    	         	 	    	      	

function putPlay() {	 	       	    	         	 	    	      	
  var exis = this.innerHTML;       	    	         	 	    	      	
  if (exis == "X" || exis == "O") {	 	       	    	         	 	    	      	
    return;	 	       	    	         	 	    	      	
  }	 	       	    	         	 	    	      	
  this.innerHTML = play;	 	       	    	         	 	    	      	
  if (play == "X") {	 	       	    	         	 	    	      	
    play = "O";	 	       	    	         	 	    	      	
  }	 	       	    	         	 	    	      	
  else {	 	       	    	         	 	    	      	
    play = "X";	 	       	    	         	 	    	      	
  }	 	       	    	         	 	    	      	
}	 	       	    	         	 	    	      	

for (i = 1; i < 10; ++i) {	 	       	    	         	 	    	      	
  document.getElementById("cell" + i).onclick = putPlay;	 	       	    	         	 	    	      	
}
\end{verbatim}
